import { SafeAreaView,
  StyleSheet, Text, 
  View,Dimensions, 
  TouchableOpacity,
  Image,
  FlatList,
  Animated ,ImageBackground} from 'react-native'
  import { LinearGradient } from 'expo-linear-gradient';
  import Back from '../assets/back.png';
import {React,useEffect,useRef,useState} from 'react'
import Ionicons from 'react-native-vector-icons/Ionicons'
import songs from '../songData'
import Slider from '@react-native-community/slider'
import {} from './musicjs'

const {width, height} = Dimensions.get('window')

const MusicPlayer = () => {


 const scrollX = useRef(new Animated.Value(0)).current;
 const [songIndex,setSongIndex] = useState(0)
 const songSlider = useRef(null)


 useEffect(()=>{
     scrollX.addListener(({value})=>{
         console.log('Scroll X',scrollX);
         console.log('Device width',width);

         const index = Math.round( value/width );
         setSongIndex(index)
         console.log('index :',index);

     })

     return ()=>{
         scrollX.removeListener()
     }
 },[])

 const skipToPrevious = ()=>{
     songSlider.current.scrollToOffset({
         offset : (songIndex - 1) * width,
     })
 }

 const skipToNext = ()=>{
     songSlider.current.scrollToOffset({
         offset : (songIndex + 1) * width,
     })
 }

 const renderSongs =({index,item})=>{
     return(
         <Animated.View style={{
             width: width,
             justifyContent: 'center',
             alignItems: 'center'
         }}>
             <View style={styles.artworkWrapper}>
               <Image source={{uri: item.img}} style={styles.artworkImg}/>
               </View>
             </Animated.View>
           )
 }
return (
 <SafeAreaView style={styles.container}>
      <LinearGradient
                start={{x: 0, y: 0.2}} end={{x: 0, y: 1}}
                // Background Linear Gradient
                colors={['#7979F2', '#FF76A1']}
                style={{position: 'absolute',
                left: 0,
                right: 0,
                top: 0,
                height:height}}
            />
     <ImageBackground
             source={Back} 
             style={{flex:1}}>
     <View style={styles.mainContainer}>

    {/* {songs.map(image => {

       })}  */}
         {/* <Image source={mcimg} style={styles.artworkImg}/> */}
         <Animated.FlatList
         ref={songSlider}
         data={songs}
         renderItem={renderSongs}
         keyExtractor={(item)=> item.at}
         horizontal
         pagingEnabled
         showsHorizontalScrollIndicator={false}
         scrollEventThrottle={16}
         onScroll={Animated.event(
             [{nativeEvent:{
                 contentOffset:{x: scrollX}
             }}],
             {useNativeDriver:true}
         )}
         />

         <View>
             <Text style={styles.title}>{songs[songIndex].title}</Text>
             <Text style={styles.at}>{songs[songIndex].at}</Text>
         </View>
       <View>
         <Slider 
         style={styles.progressContainer}
         value ={10}
         minimumValue={0}
         maximumValue={100}
         thumbTintColor="#FFD369"
         minimueTrackingColor="#FFD369"
         maximueTrackingColor="#FFF"
         onSlidingComplete={()=>{

         }}
         />
         <View style={styles.progressLabelContainer}>
             <Text style={styles.progressLabelText}>0:00</Text>
             <Text style={styles.progressLabelText}>3:55</Text>
         </View>
       </View>
     <View style={styles.musicControl}>
         <TouchableOpacity onPress={skipToPrevious}>
                 <Ionicons name='play-skip-back-outline' size={35} color = 'white' style={{marginTop:25}}/>
         </TouchableOpacity> 
         <TouchableOpacity onPress={()=>{}}>
                 <Ionicons name='ios-pause-circle' size={75} color = 'white'/>
         </TouchableOpacity> 
         <TouchableOpacity onPress={skipToNext}>
                 <Ionicons name='play-skip-forward-outline' size={35} color = 'white'style={{marginTop:25}}/>
         </TouchableOpacity> 
     </View>

     </View>
     <View style={styles.bottomContainer}>
         <View style={styles.bottomcontrol}>
             <TouchableOpacity onPress={()=>{}}>
                 <Ionicons name='heart-outline' size={30} color = 'white'/>
             </TouchableOpacity> 
             <TouchableOpacity onPress={()=>{}}>
                 <Ionicons name='repeat' size={30} color = 'white'/>
             </TouchableOpacity>
             <TouchableOpacity onPress={()=>{}}>
                 <Ionicons name='share-outline' size={30} color = 'white'/>
             </TouchableOpacity>
             <TouchableOpacity onPress={()=>{}}>
                 <Ionicons name='ellipsis-horizontal' size={30} color = 'white'/>
             </TouchableOpacity>

         </View>

     </View>
     </ImageBackground>
 </SafeAreaView>
)
}

export default MusicPlayer
const styles= StyleSheet.create({
 container:{
     flex:1,

 },
 mainContainer:{
     flex:1,
     alignItems: 'center',
     justifyContent: 'center',

 },
 title:{
    textAlign: 'center',
    color: 'white',
    fontSize: 25,
    fontWeight: 'bold',
 },
 at:{
     textAlign: 'center',
    color: 'white',
 },
 progressContainer:{
     width:350,
     height:40,
     marginTop:25,
     flexDirection:'row'
 },
 progressLabelContainer:{
     width:340,
     flexDirection:'row',
     justifyContent: 'space-between',
 },  
 progressLabelText:{
     color:'#fff'
 },
 musicControl:{
     flexDirection:'row',
     width:'60%',
     justifyContent: 'space-between',
     marginTop:15
 },
 bottomContainer:{
     borderTopColor: '#393E46',
     borderTopWidth: 1,
     width: width,
     alignItems: 'center',
     paddingVertical:15,

 },
 bottomcontrol:{
     flexDirection:'row',
     justifyContent: 'space-between',
     width: '80%'},
 artworkWrapper:{
     width: 340,
     height:400,
     marginBottom:25,

     shadowColor : '#ccc',
     shadowOffset : {
         width: 10,
         height: 5
     },
     shadowOpacity: 0.8,
     shadowRadius : 3.84,

     elevation : 5
 },
 artworkImg:{
     width:'100%',
     height:'100%',
     borderRadius:15,
 }
})
